import pandas as pd
import csv
import numpy as np
from Bio import SeqIO

def save_mem(array, text):
    if text=="mem_action":
        position = []
        proba = []
        for i in range(0,len(array)):
            position.append(array[i][0])
            proba.append(array[i][1])
        with open("position.csv", "w") as f:
            wr = csv.writer(f)
            wr.writerows(position)
        with open("proba.csv", "w") as f:
            wr = csv.writer(f)
            wr.writerows(proba)
    elif text=="mem_etat":
        with open("etat.csv", "w") as f:
            wr = csv.writer(f)
            wr.writerows(array)

def load_mem(file):
    tab = []
    with open(file, 'r') as read_obj:
        # pass the file object to reader() to get the reader object
        csv_reader = csv.reader(read_obj)
        # Pass reader object to list() to get a list of lists
        list_of_rows = list(csv_reader)
        for i in range(len(list_of_rows)):
            if list_of_rows[i] != []:
                tab.append(list_of_rows[i])
        for i in range(0, len(tab)):
            for j in range(0, len(tab[i])):
                if file == "proba.csv":
                    tab[i][j] = float(tab[i][j])
                elif file == "position.csv":
                    tab[i][j] = int(tab[i][j])
        return tab


